        public async Task<string> OTPGenerator(string email)
        {
            Random random = new();
            string otp = "";
            for (int i = 0; i < 6; i++)
            {
                otp += random.Next(0, 9).ToString();
            }
            var cacheEntryOptions = new MemoryCacheEntryOptions().SetAbsoluteExpiration(TimeSpan.FromMinutes(1));

            var cacheKey = email + otp;

            _memoryCache.Set(cacheKey, otp, cacheEntryOptions);
            return otp;
        }
        public async Task<bool> VerifyOTP(string email, string otp)
        {
            var cacheKey = email + otp;
            if (_memoryCache.TryGetValue(cacheKey, out string value))
            {
                if (value == otp)
                {
                    return true;
                }
            }
            return false;
        }
    }
